import React from 'react'
import {Field,reduxForm} from 'redux-form';
import {connect} from 'react-redux';
import {createData} from '../actions';

class AddDataForm extends React.Component {

    renderError({error,touched}){
        if (touched && error){
            return(
                <div className="ui error message">
                    <div className="header">{error}</div>
                </div>
            )
        }
    }

    renderInput = ({input,label,meta}) => {
        const className = `field ${meta.error && meta.touched ? 'error':''}`
        return (
            <div className={className}>
                <label>{label}</label>
                <input {...input} autocomplete="off"/>
                {this.renderError(meta)}
            </div>
        
        )
    }

    onSubmit = (formValues) => {
        this.props.createData(formValues)
    }

    render(){ 
        
        return (
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form error">
                <h4 class="ui dividing header">Add single shift data</h4>
                <div class="two fields">
                    <Field name="iwo" component={this.renderInput} label="enter title" placeholder="IWO No."/>
                    <Field name="client" component={this.renderInput} label="enter description" placeholder="Client Name"/>
                </div>
                <button className="ui button secondary">Submit</button>
            </form>
        )
    }
    }

const validate = (formValues) => {
    const errors = {}
    if (!formValues.iwo){
        errors.title = 'You must enter IWO No.'
    }
    if (!formValues.client){
        errors.description = 'You must enter client name'
    }

    return errors;
}

const formWrapped =  reduxForm({
    form:'addDataForm',
    validate
})(AddDataForm);


export default connect(null,{createData})(formWrapped);
